mapper.select:
SELECT * FROM schedule s JOIN movie m ON s.movie_code = m.movie_code WHERE DATE_FORMAT(start_time, '%Y년 %m월 %d') = #{start_time} AND start_time > now()
AND movie_name = #{movie_name}
ORDER BY movie_type, start_time

SELECT * FROM movie
WHERE movie_code IN (SELECT movie_code FROM schedule WHERE DATE_FORMAT(start_time, '%Y년 %m월 %d') = #{start_time} AND start_time > NOW()) GROUP BY movie_code
WHERE movie_code = #{movie_code}

SELECT *
FROM schedule
WHERE DATE_FORMAT(start_time, '%Y년 %m월 %d') = #{start_time}
AND start_time > now()

	AND movie_code = #{movie_code}
 
ORDER BY start_time
