mapper.insert:
INSERT
INTO movie
VALUES (
	#{movie_code}
	, #{movie_name}
	, TRIM(#{movie_genre})
	, TRIM(#{movie_director})
	, #{movie_actor}
	, #{release_date}
	, #{running_time}
	, #{age_limit}
	, #{movie_rating}
	, #{movie_synopsis}
	, #{movie_status}
	, #{movie_img1}
	, #{movie_img2}
	, #{movie_img3}
	, #{movie_img4}
	, #{movie_img5}
	, #{movie_trailer}
	, now()
	, #{regist_admin_id}
	, null
	, null
	, null
)

mapper.select:
SELECT *
FROM movie

	 
		WHERE ${howSearch} LIKE '%${searchKeyword}%' 
	
	
		WHERE ${columnName} = #{selectCondition}
	
	
		WHERE ${columnName} IN ( #{in1}, #{in2} )
	


	AND ${columnName2} = #{selectCondition2}


	ORDER BY ${orderColumn} ${howOrder}


	, ${orderColumn2} ${howOrder2}

SELECT COUNT(*)
FROM movie
WHERE 
	${columnName} = #{selectCondition}

	AND ${columnName2} = #{selectCondition2}

mapper.update:
UPDATE movie
SET 
	movie_status = #{movie_status}

	
		, movie_type = null	
	
	
		, movie_type = #{movie_type}			
			

WHERE movie_code = #{movie_code}

UPDATE movie
SET 
	start_screening_date = #{start_screening_date}
	, end_screening_date = #{end_screening_date}
WHERE movie_code = #{movie_code}

UPDATE movie
SET 
	movie_status = #{movie_status}
WHERE 
	movie_code IN (
	
		#{movie_code}
	
	)

UPDATE movie
SET 
	movie_status = #{movie_status},
	movie_type = #{movie_type}
WHERE 
	movie_code IN (
	
		#{movie_code}
	
	)
