mapper.insert:
INSERT
INTO movie
VALUES (
	#{movie_code}
	, #{movie_name}
	, TRIM(#{movie_genre})
	, TRIM(#{movie_director})
	, #{movie_actor}
	, #{release_date}
	, #{running_time}
	, #{age_limit}
	, #{movie_rating}
	, #{movie_synopsis}
	, #{movie_status}
	, #{movie_img1}
	, #{movie_img2}
	, #{movie_img3}
	, #{movie_img4}
	, #{movie_img5}
	, #{movie_trailer}
	, now()
	, #{regist_admin_id}
	, null
	, null
	, null
)

mapper.select:
SELECT *
FROM movie
ORDER BY movie_name ASC
LIMIT
	#{startRow}
	, #{listLimit}

SELECT *
FROM movie

	 
		WHERE ${howSearch} LIKE CONCAT('%', #{searchKeyword}, '%') 
	
	
		WHERE ${columnName} = #{selectCondition}
	
	
		WHERE ${columName} IN ( #{in1}, #{in2} )
	


	ORDER BY ${olderColumn} ${howOlder}


	, ${olderColumn2} ${howOlder2}

mapper.update:
UPDATE movie
SET movie_status = #{movie_status}
	, movie_type = #{movie_type}			
WHERE movie_code = #{movie_code}

UPDATE movie
SET start_screening_date = #{start_screening_date}
	, end_screening_date = #{end_screening_date}
WHERE movie_code = #{movie_code}
