mapper.insert:
INSERT INTO vote_box
VALUES (
	null
	, #{vote_code}
	, #{movie_code}
	, #{member_id}
	, now()
)

mapper.update:
UPDATE vote_info
SET
	vote_status = #{vote_status}
WHERE
	vote_code = #{vote_code}

mapper.select:
SELECT count(*)
FROM vote_box
WHERE 
	vote_code = #{vote_code}
AND
	member_id = #{member_id}
